// src/Signup.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Signup.css'; // Ensure this CSS file is in your src folder (or update the path accordingly)

const Signup = () => {
  const navigate = useNavigate();

  // Form state
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  
  // Placeholder handleSubmit – Replace with your actual signup logic/API call
  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would normally send the data to your signup endpoint.
    console.log("Signup data:", { name, email, password });
    // For now, redirect to login after "signup"
    navigate('/login');
  };

  return (
    <div className="container-fluid vh-100 d-flex align-items-center justify-content-center">
      <div className="row w-100 g-0">
        {/* Left Section (Hidden on Small Screens) */}
        <div className="col-md-6 d-flex flex-column justify-content-center align-items-center text-white left-section">
          <h1 className="display-1 fw-bold ecims-title">ECIMS</h1>
          <p className="lead subtitle text-center px-3">
            Extensible Computerized Inventory Management System
          </p>
        </div>
        {/* Right Section (Signup Form) */}
        <div className="col-md-6 d-flex justify-content-center align-items-center right-section">
          <div className="w-100 p-4" style={{ maxWidth: '400px' }}>
            <h2 className="mb-3">Create an Account</h2>
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="name" className="form-label">Name*</label>
                <input
                  type="text"
                  className="form-control"
                  id="name"
                  placeholder="Enter your name"
                  required
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </div>
              <div className="mb-3">
                <label htmlFor="email" className="form-label">Email*</label>
                <input
                  type="email"
                  className="form-control"
                  id="email"
                  placeholder="Enter your email"
                  required
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>
              <div className="mb-3">
                <label htmlFor="password" className="form-label">Password*</label>
                <input
                  type="password"
                  className="form-control"
                  id="password"
                  placeholder="Enter your password"
                  required
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                />
                <small className="text-muted">
                  Password must be at least 8 characters.
                </small>
              </div>
              <button type="submit" className="btn btn-primary w-100 mb-3">
                Get Started
              </button>
              <div className="text-center my-2"></div>
              <button type="button" className="btn btn-light w-100">
                <img
                  src="/images/google-icon.png"
                  alt="Google Logo"
                  className="me-2"
                  width="30"
                />
                Sign in with Google
              </button>
            </form>
            <div className="text-center mt-3">
              <span>Already have an account?</span>
              <a href="/login" className="text-primary">Log in</a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Signup;
