import React, { useEffect, useState } from 'react';

function App() {
  const [products, setProducts] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await fetch("http://127.0.0.1:8000/api/items/", {
          method: "GET",
          headers: {
            "Authorization": `Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ0b2tlbl90eXBlIjoiYWNjZXNzIiwiZXhwIjoxNzM4NjUxNTcxLCJpYXQiOjE3Mzg2NDc5NzEsImp0aSI6Ijg2YzA1NmE0NmRjMTQ4MTM5ZjliZDJiYjhhNTJlNDMyIiwidXNlcl9pZCI6M30.nBbDS2gH2-PgFEQM2weRr3SujJsRIRtolbcySvxQbMY`,  // Replace with real token
            "Content-Type": "application/json"
          }
        });

        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }

        const data = await response.json();
        setProducts(data);
      } catch (error) {
        setError(error.message);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div className="App">
      <h1>Product List</h1>
      {error ? <p style={{ color: "red" }}>{error}</p> : null}
      <ul>
        {products.length > 0 ? (
          products.map(product => (
            <li key={product.id}>{product.name} - ${product.price}</li>
          ))
        ) : (
          <p>Loading products...</p>
        )}
      </ul>
    </div>
  );
}

export default App;
