// src/App.js
import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Login from "./Login";
import ItemsList from "./ItemsList";

function App() {
  const [token, setToken] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Read token from localStorage when the app loads
    const savedToken = localStorage.getItem("accessToken");
    console.log("Token found in localStorage:", savedToken); // Debug log
    if (savedToken) {
      setToken(savedToken);
    }
    setLoading(false); // Done checking token
  }, []);

  if (loading) {
    return <h1>Loading...</h1>; // Prevent flickering while checking token
  }

  return (
    <Router>
      <Routes>
        {/* If token is null, show login; otherwise, redirect to /items */}
        <Route path="/" element={!token ? <Login setToken={setToken} /> : <Navigate to="/items" />} />
        {/* Only allow access to /items if token exists */}
        <Route path="/items" element={token ? <ItemsList /> : <Navigate to="/" />} />
      </Routes>
    </Router>
  );
}

export default App;
