// src/Dashboard.js
import React, { useState, useEffect } from 'react';

const Dashboard = ({ token }) => {
  const [products, setProducts] = useState([]);
  const [items, setItems] = useState([]);
  const [error, setError] = useState(null);

  // Log the token to verify it's being passed correctly from App.js.
  useEffect(() => {
    console.log("Token being used in Dashboard:", token);
  }, [token]);

  // Fetch products and items only when a valid token is available.
  useEffect(() => {
    if (!token) return; // Exit if token is missing

    // Function to fetch products from the backend.
    const fetchProducts = async () => {
      try {
        const response = await fetch('http://127.0.0.1:8000/api/products/', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        // Log the full response status.
        console.log("Products response status:", response.status);
        const data = await response.json();
        console.log("Fetched products:", data);
        if (!response.ok) {
          throw new Error(`Error ${response.status}: ${response.statusText}`);
        }
        setProducts(data);
      } catch (err) {
        console.error("Error fetching products:", err);
        setError(err.message);
      }
    };

    // Function to fetch items from the backend.
    const fetchItems = async () => {
      try {
        const response = await fetch('http://127.0.0.1:8000/api/items/', {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        // Log the full response status.
        console.log("Items response status:", response.status);
        const data = await response.json();
        console.log("Fetched items:", data);
        if (!response.ok) {
          throw new Error(`Error ${response.status}: ${response.statusText}`);
        }
        setItems(data);
      } catch (err) {
        console.error("Error fetching items:", err);
        setError(err.message);
      }
    };

    fetchProducts();
    fetchItems();
  }, [token]);

  return (
    <div>
      <h1>Dashboard</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <h2>Products</h2>
      {products.length > 0 ? (
        <ul>
          {products.map((product) => (
            <li key={product.id}>{product.name}</li>
          ))}
        </ul>
      ) : (
        <p>No products available.</p>
      )}
      <h2>Items</h2>
      {items.length > 0 ? (
        <ul>
          {items.map((item) => (
            <li key={item.id}>{item.name}</li>
          ))}
        </ul>
      ) : (
        <p>No items available.</p>
      )}
    </div>
  );
};

export default Dashboard;
