import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './Login';
// Import other components like Dashboard, ProductsList, etc.

function App() {
  const [token, setToken] = useState(null);

  // On app load, check if the token exists in localStorage
  useEffect(() => {
    const savedToken = localStorage.getItem('accessToken');
    if (savedToken) {
      setToken(savedToken);
    }
  }, []);

  return (
    <Router>
      <Routes>
        {/* If no token, user must log in */}
        <Route
          path="/login"
          element={!token ? <Login setToken={setToken} /> : <Navigate to="/dashboard" />}
        />
        {/* Protected route example */}
        <Route
          path="/dashboard"
          element={token ? <div>Dashboard (Protected Content)</div> : <Navigate to="/login" />}
        />
        {/* Add additional protected routes here */}
        {/* Redirect root to login */}
        <Route path="/" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
