// src/App.js
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './Login';
import Dashboard from './Dashboard';

function App() {
  const [token, setToken] = useState(null);

  // On component mount, retrieve the token from localStorage
  useEffect(() => {
    const savedToken = localStorage.getItem('accessToken');
    console.log("Token retrieved in App.js:", savedToken);
    if (savedToken) {
      setToken(savedToken);
    }
  }, []);

  // Logout function clears tokens and resets authentication state
  const logout = () => {
    localStorage.removeItem('accessToken');
    localStorage.removeItem('refreshToken');
    setToken(null);
  };

  return (
    <Router>
      <Routes>
        {/* Login Route: if token exists, redirect to Dashboard */}
        <Route
          path="/login"
          element={token ? <Navigate to="/dashboard" replace /> : <Login setToken={setToken} />}
        />
        {/* Protected Dashboard Route: only accessible if token exists */}
        <Route
          path="/dashboard"
          element={token ? <Dashboard token={token} onLogout={logout} /> : <Navigate to="/login" replace />}
        />
        {/* Fallback Route: redirect any unknown URL to /login */}
        <Route path="*" element={<Navigate to="/login" replace />} />
      </Routes>
    </Router>
  );
}

export default App;
