// src/Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import './LoginPage.css'; // Ensure this CSS file exists for custom styles

const Login = ({ setToken }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://127.0.0.1:8000/api/login/', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ username, password }),
      });
      const data = await response.json();
      if (response.ok && data.access && data.refresh) {
        // Store tokens in localStorage
        localStorage.setItem('accessToken', data.access);
        localStorage.setItem('refreshToken', data.refresh);
        setToken(data.access);
        // Navigate to the Dashboard
        navigate('/dashboard');
      } else {
        setError(data.detail || 'Login failed');
      }
    } catch (err) {
      setError('An error occurred during login.');
    }
  };

  return (
    <div className="container-fluid vh-100 d-flex align-items-center justify-content-center">
      <div className="row w-100 g-0">
        {/* Left Section */}
        <div className="col-md-6 d-flex flex-column justify-content-center align-items-center text-white left-section">
          <h1 className="display-1 fw-bold ecims-title">ECIMS</h1>
          <p className="lead subtitle text-center px-3">Extensible Computerized Inventory Management System</p>
        </div>
        {/* Right Section (Login Form) */}
        <div className="col-md-6 d-flex justify-content-center align-items-center right-section">
          <div className="w-100 p-4 login-container">
            <h2 className="mb-3">Log in to your account</h2>
            <form id="loginForm" onSubmit={handleSubmit}>
              <div className="mb-3">
                <label htmlFor="email" className="form-label">Email*</label>
                <input
                  type="email"
                  className="form-control"
                  id="email"
                  placeholder="Enter your email"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="mb-3">
                <label htmlFor="password" className="form-label">Password*</label>
                <input
                  type="password"
                  className="form-control"
                  id="password"
                  placeholder="Enter your password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <button type="submit" className="btn btn-primary w-100 mb-3">Sign in</button>
              {error && <p style={{ color: 'red' }}>{error}</p>}
              <button type="button" className="btn btn-light w-100">
                <img src="/images/google-icon.png" alt="Google Logo" className="me-2" width="30" /> Sign in with Google
              </button>
            </form>
            <div className="text-center mt-3">
              <span>Don't have an account?</span>
              <a href="/signup" className="text-primary">Sign up</a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
