// src/App.js
import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from "react-router-dom";
import Login from "./Login";
import ItemsList from "./ItemsList";
import ProductsList from "./ProductsList";
// Import other components as you create them (SuppliersList, CategoriesList, etc.)

function App() {
  const [token, setToken] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const savedToken = localStorage.getItem("accessToken");
    console.log("Token found in localStorage:", savedToken);
    if (savedToken) {
      setToken(savedToken);
    }
    setLoading(false);
  }, []);

  if (loading) {
    return <h1>Loading...</h1>;
  }

  return (
    <Router>
      {token && (
        <nav>
          <ul>
            <li>
              <Link to="/items">Items</Link>
            </li>
            <li>
              <Link to="/products">Products</Link>
            </li>
            {/* Add additional links for suppliers, categories, orders, etc. */}
          </ul>
        </nav>
      )}
      <Routes>
        <Route path="/" element={!token ? <Login setToken={setToken} /> : <Navigate to="/items" />} />
        <Route path="/items" element={token ? <ItemsList /> : <Navigate to="/" />} />
        <Route path="/products" element={token ? <ProductsList /> : <Navigate to="/" />} />
        {/* Add routes for other components such as suppliers, categories, orders, order items, and customers */}
      </Routes>
    </Router>
  );
}

export default App;
